import React from "react";

export const ContactPicker = ({ contacts, handleContactChange, value, name }) => {
  
  return (
    
      <select 
      onChange={handleContactChange} value={value} name={name}>
        <option default value={""} key={-1} >no contact selected</option>
        {
          
          contacts?.map((contact, index)=>{ 
            return <option value={contact} key={index} >{contact.name} </option>}
          )
        }
        
        
      </select>
    
  );
};
