import React from "react";

export const ContactForm = ({
  name,
  setName,
  phone,
  setPhone,
  email,
  setEmail,
  handleSubmit
}) => {
  


  return (
    <>
    <form>
    <input
      value= {name}
      type='text'
      aria-label="enter your name"
      placeholder="name"
    />
    <input
      value= {email}
      type= 'email'
      aria-label="enter your email"
      placeholder="email"
    />
    <input
      value= {phone}
      type= 'text'
      aria-label="enter your phone number"
      placeholder="phone number"
    />
    <input type="submit" value="Add" />
    </form>
    
    </>
  );
};
