import React from "react";

export const ContactPicker = ({ contacts, onChange , handleSubmit}) => {
  return (
    <select onChange={onChange} name={'contact'} value={handlesubmit} >

      <option default value={""} key={0}>No Contact Selected</option>   
      {
        contacts.map((contact, index)=>{
          return (
            <option value= {contact}  key={index}>{contact}</option> 
          )
          
        })
      }
    </select>
  );
};
